package com.class01;

public class PrimitiveDataType {

	public static void main(String[] args) {
		
		
		// TODO Auto-generated method stub
  
		/* Numeric
		 * byte- Data type what value we store 
		 * box- name of variable 
		 * 10- what is getting stored in the variable 
		 */
		
		// we are sorting whole numbers 
		byte box1=10; // -128 till 127
		
		short box2=100; // -32768 to 32767
		
		int box3=1000; // -2147483648 to 2147483647
		
		long box4=10000; // if it is to long add l after #
		
		// we are sorting floating numbers 
		
		float f=12.99f; // can use up to 5-6 decimals 
		
		double d=12.95; // up to 14-15 decimals 
		
		
		// Non numeric 
		
		// we are sorting single character values 
		
		char a= '$';
		
		char b='A'; // add anything single 
		
		char c='1';
		
		// to store boolean values 
		
		boolean variable1=true; //
		
		boolean variable=false;
		
		// i would like to store value 99 
		
		int number=99;
		
		System.out.println(d); //12.95
		
		System.out.println(a); //*

		// System.out.print(bool); Program gives error because variable bool
		//is not created at the current moment
		
		// boolean bool=true; program executes from top->bottom 
		
		System.out.println(variable1); //true
		
		System.out.println("variable"); //variablel
		
	}
}
