package com.class02;

public class NamingConvention {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//keywords - a specific word hat reserved in java memory: class, public, static, final 
		
//identifiers - name that we give to classes, variable and methods
		
		/*
		 * rule !!!
		 * do not use keyword as identifier 
		 * identifiers cannot contain space 
		 * identifiers cannot start with numbers 
		 */
		
		// 1. int final=10; error: invalid identifier
		
		// 2. byte variable one=12;
		
		// 3. short 1b=12;
		short b1=12;
		
		// 4. long *l=2000;
		// long l*=2000;
		
		long _l=2000;
		long long_=2111;
		
		double $price=12.99;
		double price$=10.99;
		
	// NamingConvention
	    // 1. we use camel casing
	    // 2. classes start with upper case and will follow camel casing 
	    // 3. variable start with lower case and will follow camel casing
		// 4. packages we use lower case and use name as reverse way of url
		// 5. use meaningful name for your variable 
		
		
		
		
		
		int Number=12; // try not to use uppercase name 
		int number=12; // more preferable
		
		int umberOne=12;
		int numberTwo=13;
		
		double applePrice=12.99;
		
		
		
		
		
		
		
		
	}

}
