package com.class07;

public class ForLoopIntro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1; i<=3; i++ ) {
			
			System.out.print("Hello ");
			
			
		}
		System.out.println();
		System.out.println("------------------Print umber from 1 to 50-------------------");
		
		for(int i=1; i<=50; i++) {
			System.out.print(i + " ");
			
		}
		System.out.println();
		System.out.println("------------------Print umber from 10 to 1-------------------");
		
		for(int i=10; i>=1; i--) {
			System.out.print(i + " ");
		
	}
		System.out.println();
		System.out.println("------------------Why my output-------------------");
		
		for(int a=0; a<=10; a+=2) {
			
			System.out.print(a+" ");
		}
		System.out.println();
		System.out.println("------------------Why my output-------------------");
		
        for(int i=5; i<=50; i+=10) {
			
			System.out.print(i+" ");
		
			
}
	}
}
