package com.class07;

public class LoopIntro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Hello");
		System.out.println("Hello");
		System.out.println("Hello");
		System.out.println("Hello");
		System.out.println("Hello");
		
		System.out.println("-------------------------------------------");
		
		int time = 17;
		
		if (time <12) {
			
			System.out.println("Hello");
			
		}
		
		System.out.println("----------------------While Loop----------------------");
		
		while (time < 12) {
			
			System.out.println("Hello");
			
			time--;
			
	}
		
		System.out.println("------------------Print umber from 1 to 10-------------------");
		
		int num = 1;
		
		while (num < 11) {
			
			System.out.println(num);
			num++;
			
		}
		
		System.out.println("------------------Print umber from 20 to 40-------------------");
		
	         num = 20;
		
		while (num <= 40) {
			
			System.out.println(num + " ");
			num++;
			
		
		}
		
		System.out.println();
		
		System.out.println("------------------Print umber from 10 to 1-------------------");
		
	int a = 10;
		
		while (a >= 1) {
			
			System.out.println(a + " ");
			a--;
			
		
	}
	
	}

}
