package com.class07;

public class Task04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println();
		System.out.println("------------------Print umber from 1 to 100-------------------");
		
		for(int i=1; i<=100; i++) {
			System.out.print(i + " ");
		
			
		
	}
		
		System.out.println();
		System.out.println("------------------Print umber from 100 to 1-------------------");
		
		for(int i=100; i>=1; i--) {
			System.out.print(i + " ");
		
		}
		
		System.out.println();
		System.out.println("------------------Print umber from 20 to 1-------------------");
		
		int a = 20;
			
			while (a >= 1) {
			
				if (a % 2 == 0) {
				System.out.print(a + " ");
				
				}	
				
				a--;
			
			}
				
				System.out.println();
				System.out.println("------------------Print umber from 20 to 1-------------------");
				
				for(int g=20; g>=1; g-=2) {
					System.out.print(g + " ");
				
		}	
		
		System.out.println();
		System.out.println("------------------Print umber from 20 to 50-------------------");
		
		int e = 20;
		
		while (e <= 50) {
			
			if (e % 2 == 0) {
			System.out.print(e+" ");
			
	}	

			e++;	
		
	}
		
		System.out.println();
		System.out.println("------------------Print umber from 20 to 50-------------------");
		
		int num = 20;

	while (num <= 50) {
		
		System.out.print(num + " ");
		num+=2;
		
	}
	}
	}

