package com.class07;

public class WhileVsDoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 6;
		
		while (num <= 5) {
			
			System.out.println(num);
			
			num++;
			
		}
		
		System.out.println("-------------------- DO WHile -----------------");
		
		int num1 = 6;
		
		do {
			
			System.out.println(num1);
			
			num1++;
			
		} while (num1 <= 5);
		
		
		}
		
	}


